import React, { useState, useEffect } from 'react';
import $ from 'jquery';
import './App.css'; // 引入自定义样式

function App() {
  // 存储所有名言
  const [quotes, setQuotes] = useState([]);
  // 当前显示的名言
  const [currentQuote, setCurrentQuote] = useState({ quote: '', author: '' });

  // 组件加载时获取名言列表
  useEffect(() => {
    $.ajax({
      url: 'https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json',
      method: 'GET',
      success: function (data) {
        // 返回的数据是字符串，需要 parse
        const jsonData = JSON.parse(data);
        setQuotes(jsonData.quotes);

        // 随机选取一条作为初始显示
        const randomIndex = Math.floor(Math.random() * jsonData.quotes.length);
        setCurrentQuote(jsonData.quotes[randomIndex]);
      },
      error: function (err) {
        console.error('获取引用时出错：', err);
      }
    });
  }, []);

  // 生成随机引用
  const getRandomQuote = () => {
    if (quotes.length === 0) return;
    const randomIndex = Math.floor(Math.random() * quotes.length);
    return quotes[randomIndex];
  };

  // 点击按钮切换引用（含淡出淡入动画）
  const fetchNewQuote = () => {
    $('#text, #author').fadeOut(500, function () {
      const randomQ = getRandomQuote();
      setCurrentQuote(randomQ);
      $('#text, #author').fadeIn(500);
    });
  };

  // 构造 Twitter 分享链接
  const tweetUrl = `https://twitter.com/intent/tweet?text=${encodeURIComponent(
    `"${currentQuote.quote}" - ${currentQuote.author}`
  )}`;

  // 构造 Tumblr 分享链接（可选）
  const tumblrUrl = `https://www.tumblr.com/widgets/share/tool?posttype=quote&tags=quotes&caption=${encodeURIComponent(
    currentQuote.author
  )}&content=${encodeURIComponent(
    currentQuote.quote
  )}&canonicalUrl=https%3A%2F%2Fwww.tumblr.com%2Fbuttons&shareSource=tumblr_share_button`;

  return (
    <div className="App">
      {/* 包裹整个随机引用生成器 */}
      <div id="quote-box">
        {/* 显示引用文本 */}
        <div id="text">“{currentQuote.quote}”</div>
        {/* 显示作者 */}
        <div id="author">- {currentQuote.author}</div>

        <div className="buttons">
          {/* 获取新引用按钮 */}
          <button id="new-quote" onClick={fetchNewQuote}>
            New Quote
          </button>

          {/* Twitter 分享按钮 */}
          <a
            id="tweet-quote"
            className="social-btn"
            href={tweetUrl}
            target="_blank"
            rel="noopener noreferrer"
            title="Tweet this quote!"
          >
            <i className="fab fa-twitter"></i>
          </a>

          {/* Tumblr 分享按钮（可选） */}
          <a
            id="tumblr-quote"
            className="social-btn"
            href={tumblrUrl}
            target="_blank"
            rel="noopener noreferrer"
            title="Post this quote on Tumblr!"
          >
            <i className="fab fa-tumblr"></i>
          </a>
        </div>
      </div>
    </div>
  );
}

export default App;
