import React, { useState, useEffect } from 'react';
import $ from 'jquery';        // 引入 jQuery
import './App.css';            // 引入自定义样式

function App() {
  // 所有名言数组
  const [quotes, setQuotes] = useState([]);
  // 当前显示的名言
  const [currentQuote, setCurrentQuote] = useState({ quote: '', author: '' });

  // 组件加载时，从 gist 链接获取 quotes 数组
  useEffect(() => {
    $.ajax({
      url: 'https://gist.githubusercontent.com/camperbot/5a022b72e96c4c9585c32bf6a75f62d9/raw/e3c6895ce42069f0ee7e991229064f167fe8ccdc/quotes.json',
      method: 'GET',
      success: function (data) {
        // 由于返回的是一个 JSON 字符串，需要手动 parse
        const jsonData = JSON.parse(data);
        setQuotes(jsonData.quotes);

        // 初次加载时随机选取一条
        const randomIndex = Math.floor(Math.random() * jsonData.quotes.length);
        setCurrentQuote(jsonData.quotes[randomIndex]);
      },
      error: function (err) {
        console.error('获取引用时出错：', err);
      }
    });
  }, []);

  // 点击按钮获取新引用（带淡出淡入动画）
  const fetchNewQuote = () => {
    // 先淡出文本和作者
    $('#text, #author').fadeOut(500, function () {
      if (quotes.length === 0) return; // 若数组还未加载好
      const randomIndex = Math.floor(Math.random() * quotes.length);
      setCurrentQuote(quotes[randomIndex]);

      // 更新完数据后再淡入
      $('#text, #author').fadeIn(500);
    });
  };

  // 构造 Tweet 链接
  const tweetUrl = `https://twitter.com/intent/tweet?text=${encodeURIComponent(
    `"${currentQuote.quote}" - ${currentQuote.author}`
  )}`;

  return (
    <div className="App">
      {/* #quote-box: 包裹整个随机引用生成器 */}
      <div id="quote-box">
        {/* #text: 显示名言 */}
        <div id="text">{currentQuote.quote}</div>
        {/* #author: 显示作者 */}
        <div id="author">- {currentQuote.author}</div>

        {/* #new-quote: 获取新名言按钮 */}
        <button id="new-quote" onClick={fetchNewQuote}>
          New Quote
        </button>

        {/* #tweet-quote: Tweet 当前名言 */}
        <a
          id="tweet-quote"
          href={tweetUrl}
          target="_blank"
          rel="noopener noreferrer"
        >
          Tweet Quote
        </a>
      </div>
    </div>
  );
}

export default App;
